import * as React from 'react';
import PropTypes from 'prop-types';
import AppBar from '@mui/material/AppBar';
import Grid from '@mui/material/Grid';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Toolbar from '@mui/material/Toolbar';
import './Header.css';
import Navbar from '../Navbar/Navbar';
import Content from '../Content/Content';
import SearchIcon from '@mui/icons-material/Search';

function Header(props) {
  const { onDrawerToggle } = props;
  const buildindlist=['Blocks','Magnetic tiles','Marble runs','Construction sets'];

  let search =document.querySelector(".search");

 search.onClick=function(){
  document.querySelector(".container").classList.toggle('active')
 }
  return (
    <React.Fragment>
      <AppBar
        sx={{
          backgroundColor: '#f3ecb059',
          boxShadow: '0 8px 32px rgba(31,28,135,0.37) ',
          backdropFilter: ' blur(8.5px)',
          borderRadius: '10px',
          marginTop:'15vh',
        }}
        position="relative"
        elevation={4}
      >
        <Toolbar>

       
            <Grid sx={{ display: { sm: 'none', xs: 'block' } }} item>
              <IconButton
                color="purple"
                aria-label="open drawer"
                onClick={onDrawerToggle}
                edge="start"
              >
                <MenuIcon />
              </IconButton>
            </Grid>


          <Grid container sx={{ display: 'flex', flexDirection: 'column'}}>
            <Grid item  sx={{ marginLeft:'8vh',marginRight:'4vh',display:'flex',justifyContent:'center'}}>
              <Navbar />
            </Grid>
          </Grid>


          <Grid>

          <IconButton size="large" aria-label="search" color="purple" calssName="Container">
            <SearchIcon />
            <i className="search"></i>
          </IconButton>
          
          </Grid>







        </Toolbar>
      </AppBar>
    </React.Fragment>
  );
}

Header.propTypes = {
  onDrawerToggle: PropTypes.func.isRequired,
};

export default Header;
