import React from "react";
import Navbarprofile from "../../Components/Navbarprofile/Navbarprofile";
import Navprofile from "../../Components/navprofile/Navprofile";
import { Box } from "@mui/system";
import { blue } from "@mui/material/colors";

const Profile = () => {
  return (
    <Box sx={{ backgroundColor: "green", display: "flex", height: "100vh" }}>
      {/* Set the Navbarprofile to be fixed */}
      <Box sx={{ position: "fixed", top: 0, zIndex: 1 }}>
        <Navbarprofile />
      </Box>

      {/* Set the Navprofile to be fixed and next to the horizontal navbar */}
      <Box sx={{ position: "fixed", top: 0, left: 0, zIndex: 1 }}>
        <Navprofile />
      </Box>

      {/* Use margin and padding to add space for the fixed navbars */}
      <Box
        sx={{
          backgroundColor: "green",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          padding: "16px",
          backgroundColor: "red",
          flexGrow: 1, // Take up remaining space
          overflowY: "auto", // Add scroll if needed
          maxHeight: `calc(100vh - ${Navbarprofile.height}px - ${Navprofile.height}px)`, // Set max height to remaining height
        }}
