import { Container } from '@mui/system'
import React from 'react'
import { Navbar } from 'react-bootstrap'
import Box from '@mui/material/Box';
import IconButton from '@mui/material/IconButton';
import Drawer from '@mui/material/Drawer';
import List from '@mui/material/List';
import ListItem from '@mui/material/ListItem';
import ListItemButton from '@mui/material/ListItemButton';
import ListItemText from '@mui/material/ListItemText';
import MenuIcon from '@mui/icons-material/Menu';

function Rechercheeheader(){

  const handleDrawerToggle = () => {
    setMobileOpen((prevState) => !prevState);
  };
  const drawer = (
    <Box onClick={handleDrawerToggle}>
      <List>
        {navItems.map((element,i) => (
          <ListItem key={i} disablePadding>
            <ListItemButton
              sx={{
                textAlign: 'center',fontfamily:'Ink Free' ,
                backgroundColor: i === activeIndex ? '#BA55D3' : 'inherit',
              }}
              onClick={() => handleListItemClick(i)}
            >
              <ListItemText primary={element.Txt} />
            </ListItemButton>
          </ListItem>
        ))}
      </List>
    </Box>
  );
  return (<Box>
    <Navbar style={{backgroundColor:"red",display:'flex',justifyContent:'center',marginBottom:'52vh',height:'100px',marginRight:'20px',width:'145vh'}}>
        <Container>

            <h1>recherche</h1>








            <Box sx={{
                    display: { sm: 'none' },
                    '& .MuiIconButton-root': {
                      color: '#9A0680',fontFamily:'Ink Free' 
                    },
                    textShadow: '0px 0px 5px #b393d3, 0px 0px 10px #b393d3, 0px 0px 10px #b393d3, 0px 0px 20px #b393d3'
                                  }}>
                <IconButton
                  aria-label="open drawer"
                  edge="end"
                  onClick={handleDrawerToggle}
                >
                  <MenuIcon />
                </IconButton>
              </Box>
        </Container>
      
    </Navbar>
     <Box component="nav">
     <Drawer
       container={container}
       variant="temporary"
       open={mobileOpen}
       onClose={handleDrawerToggle}
       ModalProps={{
         keepMounted: true, // Better open performance on mobile.
       }}
       sx={{
         display: { xs: 'block', sm: 'none' },
         '& .MuiDrawer-paper': {
           boxSizing: 'border-box',
           width: drawerWidth,
           backgroundColor: '#F3ECB0',
           color: '#F3ECB0',
           textShadow: '0px 0px 5px #b393d3, 0px 0px 10px #b393d3, 0px 0px 10px #b393d3, 0px 0px 20px #b393d3'
         }}}
       
     >
       {drawer}
     </Drawer>
   </Box>
   </Box>







  )
}

export default Rechercheeheader
