import React from "react";
import Navbarprofile from "../../Components/Navbarprofile/Navbarprofile";
import Navprofile from "../../Components/navprofile/Navprofile";
import { Box } from "@mui/system";
import { blue } from "@mui/material/colors";

import {
  MDBCol,
  MDBContainer,
  MDBRow,
  MDBCard,
  MDBCardBody,
  MDBCardImage,
  MDBBtn,
  MDBTypography,
  MDBCardText,
} from "mdb-react-ui-kit";

const Profile = () => {
  return (
    <Box sx={{ display: "flex", height: "100vh" }}>
      {/* Set the Navbarprofile to be fixed */}
      <Box sx={{ position: "fixed", top: 0, zIndex: 1 }}>
        <Navbarprofile />
      </Box>

      {/* Set the Navprofile to be fixed and next to the horizontal navbar */}
      <Box sx={{ zIndex: 1, display: "flex", justifyContent: "column" }}>
        <Box>
          <Navprofile />
        </Box>

        <Box
          sx={{
            marginTop: "64px",
            justifyContent: "center",
            alignItems: "center",
            backgroundColor: "red",
            //flexGrow: 1, // Take up remaining space
            //overflowY: "auto", // Add scroll if needed
            //width: "185vh",
            padding: "20px",
            maxWidth: "185vh",
          }}
        >
          <MDBCard>
            <div
              className="rounded-top text-white d-flex flex-row"
              style={{
                backgroundColor: "#000",
                height: "200px",
                width: "178vh",
              }}
            >
              <div
                className="ms-4 mt-5 d-flex flex-column"
                style={{ width: "150px", marginLeft: "30px" }}
              >
                <MDBCardImage
                  src=""
                  alt="Generic placeholder image"
                  className="mt-4 mb-2 img-thumbnail"
                  fluid
                  style={{ width: "150px", zIndex: "1" }}
                />
                <MDBBtn
                  outline
                  style={{
                    height: "36px",
                    overflow: "visible",
                    backgroundColor: "",
                  }}
                >
                  Edit profile
                </MDBBtn>
              </div>
              <div
                className="ms-3"
                style={{ marginTop: "130px", marginLeft: "50px" }}
              >
                <MDBTypography tag="h5">User name</MDBTypography>
              </div>
            </div>
            <div
              className="p-4 text-black"
              style={{ backgroundColor: "#f8f9fa" }}
            ></div>
            <MDBCardBody>
              <div>
                <p style={{ color: "black" }}>Bio</p>
                <div className="p-4" style={{ borderRadius: "10px solid " }}>
                  <MDBCardText style={{ color: "black" }}>
                    jhqsgduqg
                  </MDBCardText>
                </div>
              </div>
            </MDBCardBody>
          </MDBCard>
        </Box>
      </Box>
    </Box>
  );
};

export default Profile;
