import { createTheme, ThemeProvider } from "@mui/material/styles";
import useMediaQuery from "@mui/material/useMediaQuery";
import CssBaseline from "@mui/material/CssBaseline";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Link from "@mui/material/Link";
import Navigator from "../../Components/Navigator/Navigator";
import Content from "../../Components/Content/Content";
import Header from "../../Components/Header/Header";
import imge from "../../Asserts/Images/signin.png";
import Navbar from "../../Components/Navbar/Navbar.js";
import { height } from "@mui/system";
import React, { useEffect, useState } from "react";
import { CartState } from "../../context/Context";

import "./Discover.css";

let theme = createTheme({
  mixins: {
    toolbar: {
      minHeight: 80,
    },
  },
});

const drawerWidth = 256;

export default function Discover() {
  const {
    state: { toys },
  } = CartState();

  const [ages, setages] = useState([
    { id: 1, checked: false, label: "0-5" },
    { id: 2, checked: false, label: "5-10" },
    { id: 3, checked: false, label: "10-15" },
    { id: 4, checked: false, label: "+15" },
  ]);
  const handleChangeChecked = (id) => {
    console.log("im here ");
    const agesStateList = ages;
    const changeCheckedages = agesStateList.map((item) =>
      item.id === id ? { ...item, checked: !item.checked } : item
    );

    setages(changeCheckedages);
  };

  const applyFilters = () => {
    let updatedList = toys;

    const ageChecked = ages
      .filter((item) => item.checked)
      .map((item) => item.label.toLowerCase());

    if (ageChecked.length) {
      updatedList = updatedList.filter((item) => ageChecked.includes(item.age));
    }

    // Search Filter
    if (searchInput) {
      updatedList = updatedList.filter(
        (item) =>
          item.title.toLowerCase().search(searchInput.toLowerCase().trim()) !==
          -1
      );
    }

    setList(updatedList);

    !updatedList.length ? setResultsFound(false) : setResultsFound(true);
  };

  useEffect(() => {
    applyFilters();
  }, [ages, searchInput]);

  const [mobileOpen, setMobileOpen] = React.useState(false);
  const isSmUp = useMediaQuery(theme.breakpoints.up("sm"));

  const handleDrawerToggle = () => {
    console.log(mobileOpen);
    setMobileOpen(!mobileOpen);
    console.log(mobileOpen);
  };

  console.log("__________ SM here ", isSmUp);
  return (
    <ThemeProvider theme={theme}>
      <Box
        className="Boxii"
        sx={{
          display: "flex",
          backgroundImage: `url(${imge})`,
          backgroundRepeat: "no-repeat",
          backgroundSize: "cover",
          backgroundAttachment: "fixed",
        }}
      >
        <Box
          component="nav"
          sx={{ width: { sm: drawerWidth }, flexShrink: { sm: 0 } }}
        >
          {(isSmUp || !mobileOpen) && (
            <Navigator
              variant="temporary"
              open={mobileOpen}
              onClose={handleDrawerToggle}
              ages={ages}
              changeChecked={handleChangeChecked}
            />
          )}
        </Box>

        <Box
          className="red"
          sx={{
            display: "flex",
            justifyContent: "center",
            flexDirection: "column",
            width: "100%", // change to 100% width
            maxWidth: "150vh", // add max-width for larger screens
            marginTop: "2vh",
            marginLeft: "8vh",
            marginRight: "4vh",
          }}
        >
          <Header onDrawerToggle={handleDrawerToggle} />
          <Content sx={{ overflow: "auto" }} />
        </Box>
      </Box>
    </ThemeProvider>
  );
}
