import { createTheme, ThemeProvider } from '@mui/material/styles';
import useMediaQuery from '@mui/material/useMediaQuery';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Link from '@mui/material/Link';
import Navigator from '../../Components/Navigator/Navigator';
import Content from '../../Components/Content/Content';
import Header from '../../Components/Header/Header';
import imge from '../../Asserts/Images/signin.png';
import Navbar from '../../Components/Navbar/Navbar.js';
import { height } from '@mui/system';
import React, { useEffect, useState } from 'react';
import './Discover.css';



let theme = createTheme({
  

  mixins: {
    toolbar: {
      minHeight: 80,
    },
  },
});


const drawerWidth = 256;

export default function Discover() {
  const [ages, setages] = useState([
    { id: 1, checked: false, label: '0-5' },
    { id: 2, checked: false, label: '5-10' },
    { id: 3, checked: false, label: '10-15' },
    { id: 4, checked: false, label: '+15' },
  ]);
  const handleChangeChecked = (id) => {
    const agesStateList = ages;
    const changeCheckedages = agesStateList.map((item) =>
      item.id === id ? { ...item, checked: !item.checked } : item
    );
    setages(changeCheckedages);
  };





  const [mobileOpen, setMobileOpen] = React.useState(false);
  const isSmUp = useMediaQuery(theme.breakpoints.up('sm'));

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  return (
    <ThemeProvider theme={theme}>
      
      <Box className='Boxii' sx={{ display: 'flex',backgroundImage:`url(${imge})`,
   backgroundRepeat: 'no-repeat',
   backgroundSize: 'cover',
   backgroundAttachment: 'fixed' ,
   }}>
      
        <Box
          component="nav"
          sx={{ width: { sm: drawerWidth }, flexShrink: { sm: 0 } }}
        >
          {isSmUp ? null : (
            <Navigator
              PaperProps={{ style: { width: drawerWidth ,minHeight:'80vh', marginTop:'30px', overflow: 'hidden', backgroundColor:'#f3ecb059',
              boxShadow:'0 8px 32px rgba(31,28,135,0.37) ',
              backdropFilter:' blur(8.5px)',
              borderRadius:'10px'} }}
              variant="temporary"
              open={mobileOpen}
              onClose={handleDrawerToggle}
             
  
            ages={ages}
          changeChecked={handleChangeChecked}
            />
            )}
          
        { /* <Navigator
            PaperProps={{ style: { width: drawerWidth ,minHeight:'80vh', marginTop:'30px', overflow: 'hidden', backgroundColor:'#f3ecb059',
            boxShadow:'0 8px 32px rgba(31,28,135,0.37) ',
            backdropFilter:' blur(8.5px)',
            borderRadius:'10px',height:'80vh',marginTop:'17vh',marginLeft:'4vh'} }}
            sx={{ display: { sm: 'block', xs: 'none' } }}
            
          />*/}
        </Box>


        <Box className='red' sx={{
  display: 'flex',
  justifyContent: 'center',
  flexDirection: 'column',
  width: '100%',   // change to 100% width
  maxWidth: '150vh',   // add max-width for larger screens
  marginTop: '2vh',
  marginLeft: '8vh',
  marginRight: '4vh'
}}>
  <Header onDrawerToggle={handleDrawerToggle} />
  <Content  sx={{overflow:'auto'}}/>
</Box>
     </Box>
      
    </ThemeProvider>
  );
}
