import useWindowDimensions from "../../hooks/useWindowDimension";
import Typewriter from "typewriter-effect";
import "./Description.css";
import React from "react";
import Box from "@mui/material/Box";

function Description() {
  const { height } = useWindowDimensions();
  let navHeight = height / 4;

  return (
    <Box
      style={{
        marginTop: "0",
        display: "flex",
        justifyContent: "space-between",

        backgroundColor: "#46065c",
      }}
    >
      <Box
        style={{
          alignItems: "center",
          width: "300px",
          height: "300px",
          backgroundColor: "red",
          padding: "20px",
        }}
      >
        <h2>Looking for a hassle-free way to buy, sell, or exchange toys?</h2>

        <h3>Look no further than Kiddos Tradez!</h3>
      </Box>
      <Box
        style={{
          alignItems: "center",
          width: "500px",
          height: "300px",
          backgroundColor: "blue",
        }}
      >
        <h3>Look no further than Kiddos Tradez!</h3>

        <p>
          <p>
            With Kiddos Tradez,
            <br />
            you can easily browse through a vast selection of pre-loved toys,
            <br />
            sell or trade your own gently-used items,
            <br />
            and connect with a community of like-minded families who share your
            passion for play
            <br />
            <b className="bold">
              So why wait?
              <br />
              Sign up for Kiddos Tradez today and discover a whole new world of
              toy
              <br />
              buying, selling, and swapping possibilities!
            </b>
          </p>
        </p>
      </Box>
    </Box>
  );
}

export default Description;
