import * as React from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Unstable_Grid2';

import product_cart from "../data/product_data";
import './mainproduct.css';

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: 'green',
  padding: theme.spacing(1),
  textAlign: 'center',
  boxShadow:'0 8px 32px rgba(31,28,135,0.37)',
  backdropfilter:'blur(10px)',
  borderRadius:'15px',
  marginBottom: '20px',
  transition: 'all .3s ease-in',
 
  
}));

const Mainproduct = () => {
  const listItem=product_cart.map((item)=>
    <Box  sx={{ flexGrow: 1 }}>
      <Grid container spacing={3}  >
       
        <Grid container xs={12} md={7} lg={8} spacing={4}>
      
          <Grid xs={4} lg={4}>
            <Item>

              <Box
                
              >
               <img src={item.thumb} alt="Logo" sx={{ height: '20%',width:'5%', margin: '10px', flexGrow: 2 }}  />

              </Box>
              <Box  aria-labelledby="category-d" sx={{ pl: 2 }}>
              <Box className="h3">
            <h3>{item.name}</h3>
              </Box>
              <Box className="description">
              <p>{item.description}</p>
            </Box>
              <p className="price">{item.real_price}</p>
              </Box>

            </Item>
          </Grid>
        </Grid>
        
       
      </Grid>
    </Box>
  );

  return (
 
    <Box className="main_content" >
     {listItem}
    </Box>
  );    


}
export default Mainproduct;
