import * as React from 'react';
import PropTypes from 'prop-types';
import AppBar from '@mui/material/AppBar';
import Grid from '@mui/material/Grid';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Toolbar from '@mui/material/Toolbar';

import Navbar from '../Navbar/Navbar';
import Content from '../Content/Content';
import SearchIcon from '@mui/icons-material/Search';
import {
  Button,
 
  Input,
  InputGroup,

 
  
  Modal,

  ModalHeader
} from "reactstrap";

function Header(props) {
  const { onDrawerToggle } = props;
  const buildindlist=['Blocks','Magnetic tiles','Marble runs','Construction sets'];
  const [modalSearch, setmodalSearch] = React.useState(false);
  const toggleModalSearch = () => {
    setmodalSearch(!modalSearch);
  };

  let search =document.querySelector(".search");

 search.onClick=function(){
  document.querySelector(".container").classList.toggle('active')
 }
  return (
    <React.Fragment>
      <AppBar
        sx={{
          backgroundColor: '#f3ecb059',
          boxShadow: '0 8px 32px rgba(31,28,135,0.37) ',
          backdropFilter: ' blur(8.5px)',
          borderRadius: '10px',
          marginTop:'15vh',
        }}
        position="relative"
        elevation={4}
      >
        <Toolbar>

       
            <Grid sx={{ display: { sm: 'none', xs: 'block' } }} item>
              <IconButton
                color="purple"
                aria-label="open drawer"
                onClick={onDrawerToggle}
                edge="start"
              >
                <MenuIcon />
              </IconButton>
            </Grid>


          <Grid container sx={{ display: 'flex', flexDirection: 'column'}}>
            <Grid item  sx={{ marginLeft:'8vh',marginRight:'4vh',display:'flex',justifyContent:'center'}}>
              <Navbar />
            </Grid>
          </Grid>


          <InputGroup className="search-bar">
                <Button color="link" onClick={toggleModalSearch}>
                  <i className="tim-icons icon-zoom-split" />
                  <span className="d-lg-none d-md-block">Search</span>
                </Button>
              </InputGroup>
         







        </Toolbar>
      </AppBar>
      <Modal
        modalClassName="modal-search"
        isOpen={modalSearch}
        toggle={toggleModalSearch}
      >
        <ModalHeader>
          <Input placeholder="SEARCH" type="text" />
          <button
            aria-label="Close"
            className="close"
            onClick={toggleModalSearch}
          >
            <i className="tim-icons icon-simple-remove" />
          </button>
        </ModalHeader>
      </Modal>
    </React.Fragment>
  );
}

Header.propTypes = {
  onDrawerToggle: PropTypes.func.isRequired,
};

export default Header;
