import * as React from 'react';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import useMediaQuery from '@mui/material/useMediaQuery';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Link from '@mui/material/Link';
import Navigator from './Navigator.js';
import Content from './Content';
import Header from './Header';
import imge from '../../Asserts/Images/signin.png';



let theme = createTheme({
  

  mixins: {
    toolbar: {
      minHeight: 80,
    },
  },
});


const drawerWidth = 256;

export default function Discover() {
  const [mobileOpen, setMobileOpen] = React.useState(false);
  const isSmUp = useMediaQuery(theme.breakpoints.up('sm'));

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  return (
  
    
      <Box className='Boxii' sx={{ display: 'flex',backgroundImage:`url(${imge})`,
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    height: '100vh' }}>
      
        <Box
          component="nav"
          sx={{ width: { sm: drawerWidth }, flexShrink: { sm: 0 } }}
        >
          {isSmUp ? null : (
            <Navigator
              PaperProps={{ style: { width: drawerWidth ,minHeight:'80vh', marginTop:'30px', overflow: 'hidden', backgroundColor:'#f3ecb059',
              boxShadow:'0 8px 32px rgba(31,28,135,0.37) ',
              backdropFilter:' blur(8.5px)',
              borderRadius:'10px'} }}
              variant="temporary"
              open={mobileOpen}
              onClose={handleDrawerToggle}
           
          
            />
          )}

          <Navigator
            PaperProps={{ style: { width: drawerWidth ,minHeight:'80vh', marginTop:'30px', overflow: 'hidden', backgroundColor:'#f3ecb059',
            boxShadow:'0 8px 32px rgba(31,28,135,0.37) ',
            backdropFilter:' blur(8.5px)',
            borderRadius:'10px',height:'80vh',marginTop:'17vh',marginLeft:'4vh'} }}
            sx={{ display: { sm: 'block', xs: 'none' } }}
          />
        </Box>
        {/* le bleu fonc√©*/}
        <Box sx={{ flex: 1, display: 'flex', flexDirection: 'column',marginTop:'17vh' ,marginLeft:'8vh',marginRight:'4vh'}}>
        <Header onDrawerToggle={handleDrawerToggle}  />
         <Content/>
      </Box> 
      </Box>
  
  );
}
