import * as React from 'react';
import { styled } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Paper from '@mui/material/Paper';
import Grid from '@mui/material/Unstable_Grid2';

import product_cart from "../data/product_data";
import './mainproduct.css';




const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: 'green',
  padding: theme.spacing(1),
  textAlign: 'center',
  boxShadow:'0 8px 32px rgba(31,28,135,0.37)',
  backdropfilter:'blur(10px)',
  borderRadius:'15px',
  marginBottom: '30px',
  transition: 'all .3s ease-in',
  margin:"20px",
  display:'flex',
  
 
  

}));

const Item2 = styled(Paper)(({ theme }) => ({
  flexgrow:' 1',
  display: 'flex',
  alignitems: 'center',
  width:'10%',
  height:' 40%',



}));

const Mainproduct = () => {
  const listItem = product_cart.map((item) =>
    <Box sx={{ flexGrow: 1 }}>
      <Grid container spacing={2}>
        <Grid  container xs={14} md={7} lg={14} spacing={3}>
          <Grid xs={3} lg={3}></Grid>
            <Item>
              <Item2>

              <img src={item.thumb} alt="Logo" />






              </Item2>
              <Box sx={{ pl: 2}}>
                  <h3>{item.name}</h3>
                
                  <p>{item.description}</p>
              
                <p className="price">{item.real_price}</p>
              </Box>

            </Item>
          </Grid>
        </Grid>
      
    </Box>
  );

  return (
    <Box className="main_content">
      {listItem}
    </Box>
  );
}
export default Mainproduct;
