import * as React from 'react';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import useMediaQuery from '@mui/material/useMediaQuery';
import CssBaseline from '@mui/material/CssBaseline';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Link from '@mui/material/Link';
import Navigator from './Navigator.js';
import Content from './Content';
import Header from './Header';
import imge from '../../Asserts/Images/signin.png';



let theme = createTheme({
  
  typography: {
    h5: {
      fontWeight: 500,
      fontSize: 26,
      letterSpacing: 0.5,
    },
  },
  shape: {
    borderRadius: 8,
  },
  components: {
    MuiTab: {
      defaultProps: {
        disableRipple: true,
      },
    },
  },
  mixins: {
    toolbar: {
      minHeight: 48,
    },
  },
});


const drawerWidth = 256;

export default function Discover() {
  const [mobileOpen, setMobileOpen] = React.useState(false);
  const isSmUp = useMediaQuery(theme.breakpoints.up('sm'));

  const handleDrawerToggle = () => {
    setMobileOpen(!mobileOpen);
  };

  return (
    <ThemeProvider theme={theme}>
        {/* le bleu foncé*/}
      <Box className='Boxii' sx={{ display: 'flex',backgroundImage:`url(${imge})`,
    backgroundSize: 'cover',
    backgroundRepeat: 'no-repeat',
    height: '100vh' }}>
      
        <Box
          component="nav"
          sx={{ width: { sm: drawerWidth }, flexShrink: { sm: 0 } }}
        >
          {isSmUp ? null : (
            <Navigator
              PaperProps={{ style: { width: drawerWidth ,backgroundColor:'pink'} }}
              variant="temporary"
              open={mobileOpen}
              onClose={handleDrawerToggle}
           
          
            />
          )}

          <Navigator
            PaperProps={{ style: { width: drawerWidth ,backgroundColor:'pink',height:'80vh',marginTop:'17vh',marginLeft:'4vh'} }}
            sx={{ display: { sm: 'block', xs: 'none' } }}
          />
        </Box>
        {/* le bleu foncé*/}
        <Box sx={{ flex: 1, display: 'flex', flexDirection: 'column',marginTop:'17vh' ,marginLeft:'8vh',marginRight:'4vh',backgroundColor:'green'}}>
          <Header onDrawerToggle={handleDrawerToggle} />
         <Content sx={{ marginTop:'10px'}}/>
      </Box> </Box>
    </ThemeProvider>
  );
}
