import * as React from 'react';
import PropTypes from 'prop-types';
import AppBar from '@mui/material/AppBar';
import Grid from '@mui/material/Grid';
import IconButton from '@mui/material/IconButton';
import MenuIcon from '@mui/icons-material/Menu';
import Toolbar from '@mui/material/Toolbar';
import './Header.css';
import Navbar from '../Navbar/Navbar';
import Content from '../Content/Content';
import SearchIcon from '@mui/icons-material/Search';

function Header(props) {
  const { onDrawerToggle } = props;
  const buildindlist=['Blocks','Magnetic tiles','Marble runs','Construction sets'];

  return (
    <React.Fragment>
      <AppBar
        sx={{
          backgroundColor: '#f3ecb059',
          boxShadow: '0 8px 32px rgba(31,28,135,0.37) ',
          backdropFilter: ' blur(8.5px)',
          borderRadius: '10px',
          marginTop:'15vh',
        }}
        position="relative"
        elevation={4}
      >
        <Toolbar>
          {/* show menu button for small screens */}
          <Grid item sx={{ display: { xs: 'block', sm: 'none' } }}>
            <IconButton
              color="purple"
              aria-label="open drawer"
              onClick={onDrawerToggle}
              edge="start"
            >
              <MenuIcon />
            </IconButton>
          </Grid>

          {/* show navbar for large screens */}
          <Grid item sx={{ display: { xs: 'none', sm: 'block' } }}>
            <Grid container sx={{ display: 'flex', flexDirection: 'column' }}>
              <Grid item sx={{ marginLeft:'8vh',marginRight:'4vh',display:'flex',justifyContent:'center'}}>
                <Navbar />
              </Grid>
            </Grid>
          </Grid>

          {/* show search icon for small and medium screens */}
          <Grid item sx={{ display: { xs: 'block', sm: 'block', md: 'none' } }}>
            <IconButton size="large" aria-label="search" color="purple">
              <SearchIcon />
            </IconButton>
          </Grid>

          {/* show search bar for large screens */}
          <Grid item sx={{ display: { xs: 'none', sm: 'none', md: 'block' } }}>
            <SearchBar />
          </Grid>
        </Toolbar>
      </AppBar>
    </React.Fragment>
  );
}

Header.propTypes = {
  onDrawerToggle: PropTypes.func.isRequired,
};

export default Header;
